<?php/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-= Project Name : myFirstRemoting Filename :		myFirstRemotingApp.php Written by:	Seth Wilson, P.Eng Date:			May 2003 Description:	This myFirstRemotingApp object is used with Flash Remoting with AMFPHP 				to show how a user can pass data back and forth between Flash and PHP  Revisions:   1.   2.   3. Future Upgrades   1.   2.   . -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-= */class myFirstRemotingApp {        	function myFirstRemotingApp() {		$this->methodTable = array(			"getNumber" => array(				"description" => "Read in a number and pass back that number doubled",				"access" => "remote", // values are private, public, remote				"roles" => "role, list", // not used				"arguments" => array ("arg1")),			"getArray" => array("description" => "Return an Array",				"access" => "remote",// values are private, public, remote				"roles" => "role, list", // not used				"arguments" => array ("arg1")),			"getSomeText" => array(				"description" => "Read in some text and return that text with some extra",				"access" => "remote", // values are private, public, remote				"roles" => "role, list", // not used				"arguments" => array ("arg1"))			);	}function getNumber($number) {	//Read in a number and return that number doubled	return $number+$number;} // end of function getNumberfunction getSomeText($text) {	//Read in some text and return that text with some extra	return "Did you say \"".$text."\"??? \nI thought so.";}// end of function getSomeTextfunction getArray ($in) {		$testArray = $in['name'];		return array("result"=>"Name = $testArray");} // end of function getArray	} //class?>